{
  "type": "text",
  "template": "The total sales and profit for the last year are: Sales = {{sales}}, Profit = {{profit}}",
  "value_code": "df = sheets['Sheet1']
filtered = df[df['YY'] == 2023]
if filtered.empty:
    'No sales data available for 2023.'
else:
    sales = filtered['SALES_VALUE'].sum()
    profit = filtered['SALES_PROF'].sum()
    f'Sales = {sales:,.2f}, Profit = {profit:,.2f}'"
}

{
  "type": "table",
  "title": "Top 10 Customers by Sales Value",
  "code": "df = sheets['Sheet1']
if 'CUSTOMER_NAME_E' not in df.columns or 'SALES_VALUE' not in df.columns:
    pd.DataFrame({'Message': ['Required columns not found']})
else:
    customer_sales = df.groupby('CUSTOMER_NAME_E')['SALES_VALUE'].sum().reset_index()
    if customer_sales.empty:
        pd.DataFrame({'Message': ['No sales data available']})
    else:
        customer_sales = customer_sales.sort_values('SALES_VALUE', ascending=False).head(10)
        customer_sales.columns = ['Customer Name', 'Total Sales']
        customer_sales"
}

{
  "type": "chart",
  "title": "Monthly Sales Trends Across All Divisions",
  "code": "df = sheets[list(sheets.keys())[0]]
if 'MM' not in df.columns or 'YY' not in df.columns or 'DIVISION_NAME' not in df.columns or 'SALES_VALUE' not in df.columns:
    pd.DataFrame({'Message': ['Required columns not found']})
else:
    monthly_trends = df.groupby(['YY', 'MM', 'DIVISION_NAME'])['SALES_VALUE'].sum().reset_index()
    if monthly_trends.empty:
        pd.DataFrame({'Message': ['No monthly sales trend data available']})
    else:
        monthly_trends['Period'] = monthly_trends['YY'].astype(str) + '-' + monthly_trends['MM'].astype(str).str.zfill(2)
        monthly_trends = monthly_trends.sort_values(['YY', 'MM', 'DIVISION_NAME'])
        monthly_trends",
  "x": "Period",
  "y": "SALES_VALUE",
  "kind": "line",
  "category": "DIVISION_NAME"
}
